            {/* <button  type="submit">submit</button> */}
            </form>
            <div style={{}}>
                {list.map((i,key)=>(
                    <div style={{display:"flex",border:"1px solid black",width:"400px",height:"40px",fontSize:"20px",alignItems:"center"}}>
                           <input type="checkbox" checked={check} onClick={()=>(setCheck(!check))}/>
                          {check ? <del>{i}</del>:<div>{i}</div>}
                    </div>
                ))}
                <div style={{display:"flex",justifyContent:"space-between"}}>
                    <label> { list.length}</label>
                    <div style={{display:"flex",justifyContent:"space-between",width:"40%"}}>
                    <div><span>All</span></div>
                    <button><span>Active</span></button>
                    <div><span>Completed</span></div>
                    </div>
                    <div><span>Clear completed</span></div>

                </div></div>
