import React, {useState} from 'react';

const data = [
    { text: 'abc', id: 1, check: false },
    { text: 'axyz', id: 2, check: true },
]


const Todo = () =>{
    const [text, setText] = useState('');
    const [list, setList] = useState(data);
    const [filter, setFilter] = useState([]);


    const textChange = (e) => {
        setText(e.target.value);
    }

    const handleClick = e => {
        if (text) {
            // setList(list.concat(text));
            setList((prevValue) => {
                return [
                    ...prevValue,
                    { text: text, check: false, id: Math.random().toString() }
                ]
            })
            // setList([...list,{value:text, key: Math.random().toString()}])
        }

        setText('');
        e.preventDefault();
    }
    const listCompleted = (item) => {
        setFilter(list)
       let listData = list.filter(item=>{
            if(item.check){
                return item
               }
        })

        setList(listData)
        console.log(listData)
      }

      const all = (item)=>{
          let listFilterData = filter.filter(item=>{
              if(item){
                  return item
              }
              setFilter(listFilterData)
              console.log(listFilterData)
          })
      }


    //   const allList = (item) =>{
