    //   }




    const setCheck = (item) => {
        const newList = list.map(todo => {
            if (todo.id == item.id) {
                return { ...todo, check: !todo.check }
            } else {
                return { ...todo }
            }
        })
        setList(newList);
    }




    return(
        <div style={{}}>
            <h1 style={{textAlign:"center"}}>todo</h1>
            <div style={{border:"1px solid black"}}>
            <form onSubmit={handleClick}>
            <input type="text" style={{width:"300px",height:"40px",border:"none"}} value={text}  onChange={textChange} placeholder="what do we do next"/>
            {/* <button  type="submit">submit</button> */}
            </form>
            <div style={{}}>
                {list.map((item,id)=>(
                     <div style={{display:"flex",border:"1px solid black",width:"300px",height:"40px",alignItems:"center",fontSize:"20px"}} key={id}>
                           <input type="checkbox"  checked={item.check}
                            onChange={() => setCheck(item)}/>
                          {item.check ? <del>{item.text}</del>:<div>{item.text}</div>}
                    </div>
                ))}
                <div style={{display:"flex",justifyContent:"space-between",alignItems:"center"}}>
                    <label> { list.length}</label>
                    <div style={{display:"flex",justifyContent:"space-between"}}>
                    <div onClick={all}><span>All</span></div>
                    <button><span>Active</span></button>
                    <div onClick={listCompleted}><span>Completed</span></div>
                    </div>
                    <div><span>Clear completed</span></div>
                </div>
                </div>
            </div>


            {/* <div style={{display:"flex",justifyContent:"space-between"}}>
                <div>selected</div>
                <div>All</div>
                <button>Active</button>
                <div>completed</div>

            </div> */}
